mapper.select:
select count(*)
from information_schema.TABLES
where LCASE(table_name)=#{tableName}

select concat_ws(',', id, ifnull(parent,''), ifnull(code,''), ifnull(status,''), ifnull(type,''), ifnull(customer_id,''), ifnull(operator_id,''), ifnull(reseller_id,''), ifnull(user_id,''), ifnull(product_id,''), ifnull(batch,''), ifnull(query_times,''), ifnull(first_time,''), ifnull(create_time,''), ifnull(update_time,'') ) from ${tableName}
and code < #{code}
limit #{startIndex},#{pageSize}
